import React, { useState } from 'react';
import '../App.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EmployeeLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [checkMe, setCheckMe] = useState('user'); // 'user' comme valeur initiale
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('https://localhost:7013/api/Auth/login', {
                username,
                password,
                role: checkMe
            });
            if (response.status === 200) {
                console.log('Login successful');

                const data = await response.json();
                const token = data.token;

                setToken({ token });
                navigate('/productList');
            }
        } catch (error) {
            console.error('Error logging in:', error);
            if (error.response && error.response.status === 401) {
                setError(error.response.data); // Set the error message returned from the API
            } else {
                setError('Erreur lors de la connexion. Veuillez réessayer plus tard.');
            }
        }
    };

    return (
      <div className='d-flex justify-content-center align-items-center vh-100'>
          <div className='p-3 rounded w-25 border loginPage'>

              <h2 style={{ color: 'white' }}>Login</h2>
              <form onSubmit={handleLogin}>
                  <div className='mb-3'>
                      <label htmlFor='UserName'>
                          <strong style={{ color: 'white' }}>UserName</strong>
                      </label>
                      <input
                          type='text'
                          placeholder='Enter UserName'
                          name='UserName'
                          onChange={(e) => setUsername(e.target.value)}
                          className='form-control rounded-0'
                          autoComplete='off'
                      />
                  </div>
                  <div className='mb-3'>
                      <label htmlFor='password'>
                          <strong style={{ color: 'white' }}>Password</strong>
                      </label>
                      <input
                          type='password'
                          placeholder='Enter Password'
                          name='password'
                          onChange={(e) => setPassword(e.target.value)}
                          className='form-control rounded-0'
                      />
                  </div>
                  <div className='mb-3 d-flex align-items-center'> {/* Utilisez d-flex pour aligner les éléments horizontalement */}
                      <input
                          type='checkbox'
                          id='isAdmin'
                          checked={checkMe === 'admin'}
                          style={{ width: '20px', height: '20px', marginRight: '10px' }}
                          onChange={() => setCheckMe(checkMe === 'admin' ? 'user' : 'admin')}
                      />
                      <label htmlFor='isAdmin' style={{ color: 'white' }}>
                          <strong>Admin ?</strong>
                      </label>
                  </div>
                  <button type='submit' className='btn btn-success w-100 rounded-0'>
                      Log in
                  </button>
                  <div className='text-danger' style={{marginTop: '10px'}}>{error && error}</div>
              </form>
          </div>
      </div>
  );

};

export default EmployeeLogin;
