import { BrowserRouter as Router, Route } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'
import { useState } from 'react';
import './App.css';

function App() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('https://localhost:7013/api/Auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });
      if (response.ok) {
        const data = await response.json();
        console.log('okkkkkk')
        // Enregistrez le token JWT ou effectuez d'autres actions nécessaires après la connexion réussie
        navigate('/dashboard'); // Redirigez l'utilisateur vers le tableau de bord après une connexion réussie
      } else {
        // Gérez les erreurs d'authentification
        console.error('Échec de l\'authentification');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
    <div className='d-flex justify-content-center align-items-center vh-100 loginPage'>
      <div className='p-3 rounded w-25 border loginForm text-center'>
        <h2 style={{ marginBottom: '30px' }}>Hello User !</h2>
        <div className='center-input'>
          <input
            type="text"
            name="Username"
            id="Username"
            placeholder="Username."
            autoFocus="1"
            aria-label="Username."
            style={{ marginBottom: '20px' }}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className='center-input'>
          <input
            type="password"
            name="pass"
            id="pass"
            data-testid="royal_pass"
            placeholder="Mot de passe"
            aria-label="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className='d-flex justify-content-between mt-5'>
          <button className='btn btn-primary btn-lg' onClick={handleLogin}>Se connecter</button>
          <button className='btn btn-success btn-lg' style={{ marginLeft: 'auto' }} onClick={e => navigate('/login')}>S'inscrire</button>
        </div>
      </div>
    </div>
  );
}

export default App;
