import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function AddPdt({ onCreatePdt, onUpdateList }) {
  const [show, setShow] = useState(false);
  const [codePdt, setCodePdt] = useState('');
  const [produit, setProduit] = useState('');
  const [prix, setPrix] = useState('');

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const handleCreatePdt = async () => {
    try {
      const response = await axios.post('https://localhost:7013/api/Pdt/add_pdt', {
        codePdt,
        produit,
        prix
      });

      if (response.status === 200) {
        onCreatePdt(response.data);
        onUpdateList();
        toast.success('Produit ajouté avec succès');
        handleClose();
      } else {
        console.error('Erreur lors de la création du produit.');
        toast.error('Erreur lors de la création du produit.');
      }
    } catch (error) {
      console.error('Erreur lors de la création du produit:', error);
      toast.error('Erreur lors de la création du produit.');
    }
  };

  return (
    <>
      <button type="button" className="btn btn-success btn-lg" style={{ marginBottom: '20px' }} onClick={handleShow}>
        <span className="icon">
          <FontAwesomeIcon icon={faPlus} />
        </span>{" "}
        Ajouter
      </button>

      <Modal show={show} onHide={handleClose} backdrop="static">
        <Modal.Header closeButton>
          <Modal.Title>Ajouter un produit</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="formCodePdt">
              <Form.Label>Code produit</Form.Label>
              <Form.Control type="text" placeholder="Entrez le code produit" value={codePdt} onChange={(e) => setCodePdt(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="formProduit">
              <Form.Label>Produit</Form.Label>
              <Form.Control type="text" placeholder="Entrez le nom du produit" value={produit} onChange={(e) => setProduit(e.target.value)} />
            </Form.Group>
            <Form.Group controlId="formPrix">
              <Form.Label>Prix</Form.Label>
              <Form.Control type="text" placeholder="Entrez le prix du produit" value={prix} onChange={(e) => setPrix(e.target.value)} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Annuler
          </Button>
          <Button variant="primary" onClick={handleCreatePdt}>
            Enregistrer
          </Button>
        </Modal.Footer>
      </Modal>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </>
  );
}

export default AddPdt;
