import React, { useState } from 'react';
import '../App.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const EmployeeLogin = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('https://localhost:7013/api/Auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });
            if (response.ok) {
                console.log('Login successful');
                navigate('/productList');
            } else {
                const errorData = await response.json();
                setError(errorData.message); // Assuming error message is returned from server
            }
        } catch (error) {
            console.error('Error logging in:', error);
            setError('Error logging in. Please try again later.');
        }
    };

    return (
        <div className='d-flex justify-content-center align-items-center vh-100'>
            <div className='p-3 rounded w-25 border loginPage'>
                <div className='text-danger'>{error && error}</div>
                <h2 style={{color:'white'}}>Login</h2>
                <form onSubmit={handleLogin}>
                    <div className='mb-3'>
                        <label htmlFor='UserName'>
                            <strong style={{color:'white'}}>UserName</strong>
                        </label>
                        <input
                            type='text'
                            placeholder='Enter UserName'
                            name='UserName'
                            onChange={(e) => setUsername(e.target.value)}
                            className='form-control rounded-0'
                            autoComplete='off'
                        />
                    </div>
                    <div className='mb-3'>
                        <label htmlFor='password'>
                            <strong style={{color:'white'}}>Password</strong>
                        </label>
                        <input
                            type='password'
                            placeholder='Enter Password'
                            name='password'
                            onChange={(e) => setPassword(e.target.value)}
                            className='form-control rounded-0'
                        />
                    </div>
                    <button type='submit' className='btn btn-success w-100 rounded-0'>
                        Log in
                    </button>
                    <p>You agree to our terms and policies.</p>
                </form>
            </div>
        </div>
    );
};

export default EmployeeLogin;
