import React, { useState } from 'react'
import '../App.css'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

    const EmployeeLogin = () => {
    const [isLabelVisible, setIsLabelVisible] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate()
    axios.defaults.withCredentials = false;
    const [error, setError] = useState('')

        const handleLogin = async () => {
          try {
            const response = await fetch('https://localhost:7013/api/Auth/login', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              body: JSON.stringify({ username, password }),
            });
            if (response.ok) {
              console.log('Login successful');
              navigate('/productList');
            } else {
              console.error('Authentication failed');
            }
          } catch (error) {
            console.error('Error logging in:', error);
          }
        };


    return (
        <div className='d-flex justify-content-center align-items-center vh-100'>
            <div className='p-3 rounded w-25 border loginPage'>
                <div className='text-danger'>
                    {error && error}
                </div>
                <h2>Login</h2>
                <form onSubmit={handleSubmit}>
                    <div className='mb-3'>
                        <label htmlFor="UserName"><strong>UserName</strong></label>
                        <input type="text" placeholder='Enter UserName' name='UserName' 
                          onChange={e => setValues({...values, UserName: e.target.value})} className='form-control rounded-0' autoComplete='off'/>
                    </div>
                    <div className='mb-3'>
                        <label htmlFor="password"><strong>Password</strong></label>
                        <input type="password" placeholder='Enter Password' name='password'
                          onChange={e => setValues({...values, password: e.target.value})} className='form-control rounded-0' />
                    </div>
                    <button type='submit' className='btn btn-success w-100 rounded-0'> Log in</button>
                    {isLabelVisible && <label>Label visible</label>}
                    <p>You are agree to aour terms and policies</p>
                </form>
            </div>
        </div>
    )
}

export default EmployeeLogin
