import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AddPdt = ({ handleClose }) => {
  // Fonction pour gérer la soumission du formulaire
  const handleSubmit = (e) => {
    e.preventDefault();
    // Logique de soumission du formulaire
    // ...
    // Fermer le popup après la soumission
    handleClose();
  };

  return (
    <>
      <Modal show={true} onHide={handleClose} backdrop="static">
        <Modal.Header closeButton>
          <Modal.Title>Ajouter un produit</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formItem">
              <div className="row mt-3">
                <div className="col-md-4">
                  <Form.Label>Type Produit:</Form.Label>
                </div>
                <div className="col-md-8">
                  <Form.Control type="text" placeholder="Type" />
                </div>
              </div>
            </Form.Group>
            <Form.Group controlId="formItem">
              <div className="row mt-3">
                <div className="col-md-4">
                  <Form.Label>Lieu:</Form.Label>
                </div>
                <div className="col-md-8">
                  <Form.Control type="text" placeholder="Lieu" />
                </div>
              </div>
            </Form.Group>
            <Form.Group controlId="formItem">
              <div className="row mt-3">
                <div className="col-md-4">
                  <Form.Label>Site:</Form.Label>
                </div>
                <div className="col-md-8">
                  <Form.Control type="text" placeholder="Site" />
                </div>
              </div>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Annuler
          </Button>
          <Button variant="primary" onClick={handleSubmit}>
            Enregistrer
          </Button>
        </Modal.Footer>
      </Modal>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </>
  );
};

export default AddPdt;
