// reunionActions.js
import { ADD_PDT, FETCH_Pdts,SET_TOKEN, DELETE_REUNIONS, UPDATE_REUNIONS } from '../constants/constants';
import axios from 'axios';


export const login = (username, password, role) => {
  return async (dispatch) => {
    try {
      const response = await axios.post('https://localhost:7013/api/Auth/login', {
        username,
        password,
        role
      });
      if (response.status === 200) {
        const token = response.data.token;
        dispatch(setToken(token)); // Dispatchez l'action pour stocker le token dans le store Redux
        // Facultatif : Redirigez vers une autre page après la connexion réussie
      }
    } catch (error) {
      console.error('Error logging in:', error);
      // Gestion des erreurs
    }
  };
};

export const setToken = (token) => {
  return {
    type: SET_TOKEN,
    payload: token
  };
};

export const addPdt = (product) => {
  return {
    type: ADD_PDT,
    payload: product,
  };
};

// Action creator pour récupérer les réunions depuis l'API
export const fetchPdt = () => {
  return async (dispatch) => {
    try {
      // Effectuez votre appel à l'API ici
      const response = await axios.get('https://localhost:7013/api/Product/GetProductsById');
      console.log('aaaaaaaaaa',response)
      // Dispatchez l'action FETCH_REUNIONS avec les données récupérées
      dispatch({
        type: FETCH_Pdts,
        payload: response.data
      });
    } catch (error) {
      console.error('Error fetching Produits:', error);
    }
  };
};
