import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { fetchPdt } from '../Actions/PdtActions';
import { useNavigate } from 'react-router-dom';
import { MDBDatatable } from 'mdb-react-ui-kit';
import { UseSelector } from 'react-redux';

function ProductList() {
  const dispatch = useDispatch();
  const [currentPage, setCurrentPage] = useState(1);
  const usersPerPage = 10;
  const [basicData, setBasicData] = useState(null);

  useEffect(() => {
    dispatch(fetchPdt());
  }, [dispatch]);

  const products = UseSelector(state => state.products.products);

  useEffect(() => {
    if (products.length > 0) {
      const formattedData = {
        columns: ['Id', 'Code produit', 'Produit', 'Prix'],
        rows: products.map(product => [product.id, product.codePdt, product.produit, product.prix])
      };
      setBasicData(formattedData);
    }
  }, [products]);

  const navigate = useNavigate();
  const handleInLogin = async () => {
    try {
      const response = await fetch('https://localhost:7013/api/Auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        }});
      if (response.ok) {
        console.log('byyy')
        // Enregistrez le token JWT ou effectuez d'autres actions nécessaires après la connexion réussie
        navigate('/Home'); // Redirigez l'utilisateur vers le tableau de bord après une connexion réussie
      } else {
        // Gérez les erreurs d'authentification
        console.error('Échec de l\'authentification');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
    <div className="container">
      <div className="row">
        <div className='col'></div>
        <div className="col-auto">
          <button type="button" className="btn btn-primary btn-lg ml-auto" onClick={handleInLogin}>Se deconnecter</button>
        </div>
      </div>
      <h1 style={{ textAlign: 'center', marginBottom: '150px' }}>Liste des produits</h1>
      {basicData && (
        <MDBDatatable data={basicData} />
      )}
    </div>
  );
};

export default ProductList;
