import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function AddPdt({ onCreatePdt, onUpdateList }) {
  const [codePdt, setCodePdt] = useState('');
  const [produit, setProduit] = useState('');
  const [prix, setPrix] = useState('');

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => {
    setShow(true);
  };

  const handleCreatePdt = async () => {
    try {
        const response = await axios.post('https://localhost:7013/api/Reunion/add_reunion11', null, {
          params: {
            code_Pdt: codePdt,
            Produit: produit,
            Prix: prix
          }
        });
        console.log('Creating Pdt...');

        if (response.status === 200) {
          const newPdt = {
            codePdt: codePdt,
            produit: produit,
            prix: prix
          };

          onCreatePdt(newPdt);
          onUpdateList(); 

          toast.success('Ajout effectué avec succès');
          // Reset the form after successful creation
          setTypeReunion('');
          setDatePrev('');
          setDateReal('');
          setLieu('');
          setSite('');
          handleClose(); // Close the modal
          console.log('Response:', response);
        } else {
          console.error('Error while creating reunion.');
          toast.error('Error while creating reunion.');
        }
      } catch (error) {
        console.error('Error while creating reunion:', error);
        toast.error('Error while creating reunion.');
      }
  };

  return (
    <>
      <button type="button" className="btn btn-success btn-lg" style={{ marginBottom: '20px' }} onClick={handleShow}>
        <span className="icon">
          <FontAwesomeIcon icon={faPlus} />
        </span>{" "}
        Ajouter
      </button>

      <Modal show={show} onHide={handleClose} backdrop="static">
      <Modal.Header closeButton>
<Modal.Title>Ajouter un réunion</Modal.Title>
</Modal.Header>
<Modal.Body>
</Modal.Body>
<Modal.Footer>
<Button variant="secondary" onClick={handleClose}>
Annuler
</Button>
<Button variant="primary" onClick={handleCreatePdt}>
Enregistrer
</Button>
</Modal.Footer>
</Modal>


      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </>
  );
}

export default AddPdt;
