import { BrowserRouter as Router, Route, withRouter } from 'react-router-dom';

function App() {
  const handleEmployeeLogin = () => {
    // Vous pouvez accéder à l'historique via props.history
    // et naviguer vers la page employeeLogin
    props.history.push('/employeeLogin');
  };

  const handleAdminLogin = () => {
    // Naviguer vers la page login
    props.history.push('/login');
  };

  return (
    <Router>
      <div className='d-flex justify-content-center align-items-center vh-100 loginPage'>
        <div className='p-3 rounded w-25 border loginForm text-center'>
          <h2>Login As</h2>
          <div className='d-flex justify-content-between mt-5'>
            <button className='btn btn-primary btn-lg' onClick={handleEmployeeLogin}>Employee</button>
            <button className='btn btn-success btn-lg' onClick={handleAdminLogin}>Admin</button>
          </div>
        </div>
      </div>
    </Router>
  );
}

// Utilisez withRouter pour envelopper le composant App et lui fournir l'historique
export default withRouter(App);
