import React from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlus } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useState } from 'react';

function AddPdt({ onCreatePdt, onUpdateList }) {
  const [codePdt, setCodePdt] = useState('');
  const [produit, setProduit] = useState('');
  const [prix, setPrix] = useState('');

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => {
    setShow(true);
  };

  const handleCreatePdt = async () => {
    try {
      const response = await axios.post('https://localhost:7013/api/Product/AddProduct', {
        CodePdt: codePdt,
        Produit: produit,
        Prix: prix
      });
        console.log('Creating Pdt...');

        if (response.status === 200) {
          const newPdt = {
            CodePdt: codePdt,
            Produit: produit,
            Prix: prix
          };

          onCreatePdt(newPdt);
          onUpdateList(); 

          toast.success('Ajout effectué avec succès');
          // Reset the form after successful creation
          setCodePdt('');
          setProduit('');
          setPrix('');
          handleClose(); // Close the modal
          console.log('Response:', response);
        } else {
          console.error('Error while creating reunion.');
          toast.error('Error while creating pdt.');
        }
      } catch (error) {
        console.error('Error while creating reunion:', error);
        toast.error('Error while creating produit.');
      }
  };

  return (
    <>
      <button type="button" className="btn btn-success btn-lg" style={{ marginBottom: '20px' }} onClick={handleShow}>
        <span className="icon">
          <FontAwesomeIcon icon={faPlus} />
        </span>{" "}
        Ajouter
      </button>

      <Modal show={show} onHide={handleClose} backdrop="static">
      <Modal.Header closeButton>
<Modal.Title>Ajouter un produit</Modal.Title>
</Modal.Header>
<Modal.Body>
<Form.Group controlId="formItem">
<div className="row mt-3">
<div className="col-md-4">
<Form.Label>Code de produit:</Form.Label>
</div>
<div className="col-md-8">
<Form.Control
type="text"
placeholder="Code pdt"
value={codePdt} 
onChange={(e) => setCodePdt(e.target.value)}
/>
</div>
</div>
</Form.Group>
<Form.Group controlId="formItem">
<div className="row mt-3">
<div className="col-md-4">
<Form.Label>Nom de produit:</Form.Label>
</div>
<div className="col-md-8">
<Form.Control
type="text"
placeholder="Name pdt"
value={produit} 
onChange={(e) => setProduit(e.target.value)}
/>
</div>
</div>
</Form.Group>
<Form.Group controlId="formItem">
<div className="row mt-3">
<div className="col-md-4">
<Form.Label>Prix de produit:</Form.Label>
</div>
<div className="col-md-8">
<Form.Control
type="text"
placeholder="prix pdt"
value={prix} 
onChange={(e) => setPrix(e.target.value)}
/>
</div>
</div>
</Form.Group>
</Modal.Body>
<Modal.Footer>
<Button variant="secondary" onClick={handleClose}>
Annuler
</Button>
<Button variant="primary" onClick={handleCreatePdt}>
Enregistrer
</Button>
</Modal.Footer>
</Modal>


      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />
    </>
  );
}

export default AddPdt;
