import { BrowserRouter as Router, Route } from 'react-router-dom';
import { useNavigate } from 'react-router-dom'
import { useState } from 'react';
import '../App.css';
import Link from 'next/link';
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBInput,
  MDBCheckbox,
  MDBIcon
}
from 'mdb-react-ui-kit';

function Home() {
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async () => {
    try {
      const response = await fetch('https://localhost:7013/api/Auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ username, password })
      });
      if (response.ok) {
        const data = await response.json();
        console.log('okkkkkk')
        // Enregistrez le token JWT ou effectuez d'autres actions nécessaires après la connexion réussie
        navigate('/productList'); // Redirigez l'utilisateur vers le tableau de bord après une connexion réussie
      } else {
        // Gérez les erreurs d'authentification
        console.error(password,'Échec de l\'authentification');
      }
    } catch (error) {
      console.error('Erreur lors de la connexion :', error);
    }
  };

  return (
<MDBContainer className="p-3 my-5 d-flex flex-column w-50">

<MDBInput wrapperClass='mb-4' label='UserName' id='form1' type='UserName' onChange={(e) => setUsername(e.target.value)}/>
<MDBInput wrapperClass='mb-4' label='Password' id='form2' type='password' onChange={(e) => setPassword(e.target.value)}/>

<div className="d-flex justify-content-between mx-3 mb-4">
  <MDBCheckbox name='flexCheck' value='' id='flexCheckDefault' label='Remember me' />
  <a href="!#">Forgot password?</a>
</div>

<MDBBtn className="mb-4">Sign in</MDBBtn>

<div className="text-center">
<p>Not a member? <Link to="/Registration">Register</Link></p>
</div>

</MDBContainer>

  );
}

export default Home;
